import React from 'react';
import ReactDOM from 'react-dom';
const ReactMarkdown = require('react-markdown')

class ListRecipeLink extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      button_clicked: true,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleClick() {
    //this.props.onClick();
    alert('clicked');
  }

  render() {
    <button onclick={this.handleClick}/>
  }

}

class ListRecipe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipe_text: '',
    };
  }

  render() {
    return (
      <ReactMarkdown source={this.state.recipe_text}
      />
    );
  }

  componentDidMount() {
    const link = this.props.link;
    fetch(link)
      .then(response => response.blob())
        .then(blob => blob.text())
          .then(text => this.setState({recipe_text: text}));
  }
}


ReactDOM.render(
  <ListRecipeLink />,
  //<ListRecipe
  //  link='https://katie-matt-recipes.s3.amazonaws.com/Barbacoa+(Instant+Pot).md'
  ///>,
  document.getElementById('root')
);
