import React from 'react';
import ReactDOM from 'react-dom';
const ReactMarkdown = require('react-markdown')

class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipes: [
        {
          clicked: false,
          link: 'https://katie-matt-recipes.s3.amazonaws.com/Barbacoa+(Instant+Pot).md'
        },
        {
          clicked: false,
          link: 'https://katie-matt-recipes.s3.amazonaws.com/.md'
        }
      ]
    }
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState({button_clicked: true});
  }

  render() {
    const button_clicked = this.state.button_clicked;
    if (button_clicked) {
      return (
        <ListRecipe
          link='https://katie-matt-recipes.s3.amazonaws.com/Barbacoa+(Instant+Pot).md'
        />
      )
    }
    return (
      <ListRecipeLink onClick={this.handleClick} />
    )
  }
}
class ListRecipeLink extends React.Component {
  constructor(props) {
    super(props);
    //this.handleClick = this.handleClick.bind(this);
  }

  //handleClick() {
  //  //this.props.onClick();
  //  alert('clicked');
  //}

  render() {
    return (
      <button onClick={this.props.onClick}/>
    );
  }

}

class ListRecipe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipe_text: '',
    };
  }

  render() {
    return (
      <ReactMarkdown source={this.state.recipe_text}
      />
    );
  }

  componentDidMount() {
    const link = this.props.link;
    fetch(link)
      .then(response => response.blob())
        .then(blob => blob.text())
          .then(text => this.setState({recipe_text: text}));
  }
}


