class ListRecipe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      text: '',
    };
  }

  render() {
    return (
      <ReactMarkdown source={this.state.text}
      />
    );
  }

  componentDidMount() {
    fetch(this.props.link)
      .then(response => response.blob())
        .then(blob => blob.text())
          .then(text => this.setState({recipe: text}));
  }
}


ReactDOM.render(
  <ListRecipe
    link='https://katie-matt-recipes.s3.amazonaws.com/Barbacoa+(Instant+Pot).md'
  />,
  document.getElementById('root')
);
