import React from 'react';
import ReactDOM from 'react-dom';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container'
import 'bootstrap/dist/css/bootstrap.min.css';

const ReactMarkdown = require('react-markdown');

class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipes: [
        {
          link: 'https://katie-matt-recipes.s3.amazonaws.com/Barbacoa+(Instant+Pot).md',
          description: "Barbacoa (Insant Pot)"
        },
        {
          link: 'https://katie-matt-recipes.s3.amazonaws.com/Basic+Pan+Sauce.md',
          description: "Basic Pan Sauce"
        }
      ],
      recipe_index: null,
      show_links: true,
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(index) {
    this.setState({recipe_index: index});
    this.setState({show_links: false});
  }

  render() {
    const show_links = this.state.show_links;
    if (show_links) {
      const recipes = this.state.recipes;
      const links = recipes.map((recipe, index) => 
        <ListRecipeLink
          onClick={this.handleClick}
          key={index}
          index={index}
          description={recipe.description}
        />
      );
      return (
        <Container fluid>
          {links}
        </Container>
      );
    }
    else {
      return (
        <ListRecipe
          link={this.state.recipes[this.state.recipe_index].link}
        />
      );
    }
  }

}

class ListRecipeLink extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.props.onClick(this.props.index);
  }

  render() {
    return (
      <Row>
        <Button
          variant="primary"
          onClick={this.handleClick}
        >
          {this.props.description}
        </Button>
      </Row>
    );
  }

}

class ListRecipe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipe_text: '',
    };
  }

  render() {
    return (
      <ReactMarkdown source={this.state.recipe_text} />
    );
  }

  componentDidMount() {
    const link = this.props.link;
    fetch(link)
      .then(response => response.blob())
        .then(blob => blob.text())
          .then(text => this.setState({recipe_text: text}));
  }
}


