import React from 'react';
import ReactDOM from 'react-dom';
import Button from 'react-bootstrap/Button';
const ReactMarkdown = require('react-markdown')

class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipes: [
        {
          link: 'https://katie-matt-recipes.s3.amazonaws.com/Barbacoa+(Instant+Pot).md'
        },
        {
          link: 'https://katie-matt-recipes.s3.amazonaws.com/Basic+Pan+Sauce.md'
        }
      ],
      recipe_index: null,
      show_links: true,
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(index) {
    this.setState({recipe_index: index});
    this.setState({show_links: false});
  }

  render() {
    const show_links = this.state.show_links;
    if (show_links) {
      const recipes = this.state.recipes;
      const links = recipes.map((recipe, index) => 
        <ListRecipeLink onClick={this.handleClick} key={index} index={index} link={recipe.link} />
      );
      return (
        <div>
          {links}
        </div>
      );
    }
    else {
      return (
        <ListRecipe
          link={this.state.recipes[this.state.recipe_index].link}
        />
      );
    }
  }

}

class ListRecipeLink extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.props.onClick(this.props.index);
  }

  render() {
    return (
      <button variant="primary" onClick={this.handleClick}>
        Barbacoa
      </button>
    );
  }

}

class ListRecipe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipe_text: '',
    };
  }

  render() {
    return (
      <ReactMarkdown source={this.state.recipe_text} />
    );
  }

  componentDidMount() {
    const link = this.props.link;
    fetch(link)
      .then(response => response.blob())
        .then(blob => blob.text())
          .then(text => this.setState({recipe_text: text}));
  }
}


